        cv::line(image, cvPoint(squares[i][2].x,
                                squares[i][2].y),
                 cvPoint(squares[i][2].x,
                         squares[i][2].y - lineLength),
                 cvScalar(255, 255, 255, 0), 8, 4);
        cv::line(image, cvPoint(squares[i][2].x,
                                squares[i][2].y),
